mapper.insert:

         select HOSTCLASS_SEQ.nextval from dual


		insert into
		HOSTCLASS(
         C_NO
         , C_NAME
         , CATEGORY_CODE
         , H_NO
         , L_NO
         , C_PRICE
         , PPNUM
         , C_START_TIME
         , C_TIME
         , END_FLAG
         )
		values( #{cNo}
            , #{cName}
            , #{CateCode}  
            , #{hNo}
            , #{lNo}
             , #{cPrice}
             , #{ppnum}
             , #{cStart}
             , #{cTime}
             , #{endFlag}
		)


         select C_CONTENTS_SEQ.nextval from dual


		insert 
		into C_Contents(
           CONTENTS_NO
           , C_NO
           , THUMBNAIL
           ,CONTENTS_FILENAME1 
           ,CONTENTS_FILENAME2
           ,CONTENTS_FILENAME3 
           ,CONTENTS_FILENAME4 
           ,CONTENTS_FILENAME5
           ,CONTENTS_CONTENT
           )
        values(
              #{contentsNo}
              , #{cNo}
              , #{thumbnail}
              ,#{cFilename1}
              ,#{cFilename2}
              ,#{cFilename3}
              ,#{cFilename4}
              ,#{cFilename5}
              ,#{content}
           )


         select LOCATION_SEQ.nextval from dual

		insert
		 into Location(
           L_NO
           , ZIPCODE
           , L_ADDRESS
           , L_ADDRESS_DETAIL
           , H_NO
           )
         values(
            #{lNo}
            , #{zipcode}
            , #{lAddress}
            , #{lAddressDetail}
            ,#{hNo}
            )
